example.code:
@inject ContextMenuService ContextMenuService
<RadzenButton Text="Show context menu" ContextMenu=@(args => ShowContextMenuWithContent(args)) />
@code {
    void ShowContextMenuWithContent(MouseEventArgs args) => ContextMenuService.Open(args, ds =>
      @<RadzenMenu Click="OnMenuItemClick">
        <RadzenMenuItem Text="Item1" Value="1"></RadzenMenuItem>
        <RadzenMenuItem Text="Item2" Value="2"></RadzenMenuItem>
        <RadzenMenuItem Text="More items" Value="3">
            <RadzenMenuItem Text="More sub items" Value="4">
                <RadzenMenuItem Text="Item1" Value="5"></RadzenMenuItem>
                <RadzenMenuItem Text="Item2" Value="6"></RadzenMenuItem>
            </RadzenMenuItem>
        </RadzenMenuItem>
      </RadzenMenu>);

    void OnMenuItemClick(MenuItemEventArgs args)
    {
      Console.WriteLine($"Menu item with Value={args.Value} clicked");
    }
}

@inject DialogService DialogService
<RadzenButton Text="Show dialog with inline Blazor content" Click=@ShowInlineDialog />
@code {
 async Task ShowInlineDialog()
 {
   var result = await DialogService.OpenAsync("Simple Dialog", ds =>
     @<div>
         <p Style="margin-bottom: 1rem">Confirm?</p>
         <div class="row">
             <div class="col-md-12">
                 <RadzenButton Text="Ok" Click="() => ds.Close(true)" Style="margin-bottom: 10px; width: 150px" />
                 <RadzenButton Text="Cancel" Click="() => ds.Close(false)" ButtonStyle="ButtonStyle.Base"  Style="margin-bottom: 10px; width: 150px"/>
                 <RadzenButton Text="Refresh" Click="(() => { orderID = 10249; ds.Refresh(); })" ButtonStyle="ButtonStyle.Info"  Style="margin-bottom: 10px; width: 150px"/>
                 Order ID: @orderID
             </div>
         </div>
     </div>);
   Console.WriteLine($"Dialog result: {result}");
 }
}

var builder = WebApplication.CreateBuilder(args);
var activator = new RadzenComponentActivator();
// Replace RadzenButton with MyButton
activator.Override<RadzenButton, MyButton>();
// Replace RadzenDataGrid with MyDataGrid
activator.Override(typeof(RadzenDataGrid<>), typeof(MyDataGrid<>));
// Register the activator
builder.Services.AddSingleton<IComponentActivator>(activator);

@inject TooltipService tooltipService
<RadzenButton Text="Show tooltip" MouseEnter="@(args => ShowTooltipWithHtml(args, new TooltipOptions(){ Style = "color:#000", Duration = null }))" />
@code {
    void ShowTooltipWithHtml(ElementReference elementReference, TooltipOptions options = null) => tooltipService.Open(elementReference, ds =>
        @<div>
            Some<b>HTML</b> content
        </div>, options);
    }
}
